import React, { Component } from "react";
import "./Skills.css";
import SoftwareSkill from "../../components/softwareSkills/SoftwareSkill";
import { skills } from "../../portfolio";
import { Fade } from "react-reveal";
import DataScienceImg from "./DataScienceImg";
import FullStackImg from "./FullStackImg";
import CloudInfraImg from "./CloudInfraImg";
import DesignImg from "./DesignImg";

function GetSkillImage(props) {
  const theme = props.theme;
  const fileName = props.fileName;

  // Map of fileNames to SVG components
  const svgComponents = {
    DataScienceImg: <DataScienceImg theme={theme} />,
    FullStackImg: <FullStackImg theme={theme} />,
    CloudInfraImg: <CloudInfraImg theme={theme} />,
    DesignImg: <DesignImg theme={theme} />,
  };

  if (svgComponents[fileName]) {
    // If the fileName matches a key in svgComponents, render the SVG component
    return svgComponents[fileName];
  } else {
    // Otherwise, assume it's an image file and render it using <img>
    return (
      <img
        alt={fileName}
        src={require(`../../assests/images/${artisan_ui.jpg}`)}
        className="skills-image"
      />
    );
  }
}

class SkillSection extends Component {
  render() {
    const theme = this.props.theme;
    return (
      <div>
        {skills.data.map((skill, i) => {
          return (
            <div key={i} className="skills-main-div">
              <Fade left duration={2000}>
                <div className="skills-image-div">
                  <GetSkillImage fileName={skill.fileName} theme={theme} />
                </div>
              </Fade>

              <div className="skills-text-div">
                <Fade right duration={1000}>
                  <h1 className="skills-heading" style={{ color: theme.text }}>
                    {skill.title}
                  </h1>
                </Fade>
                <Fade right duration={1500}>
                  <SoftwareSkill logos={skill.softwareSkills} />
                </Fade>
                <Fade right duration={2000}>
                  <div>
                    {skill.skills.map((skillSentence, i) => {
                      return (
                        <p
                          key={i}
                          className="subTitle skills-text"
                          style={{ color: theme.secondaryText }}
                        >
                          {skillSentence}
                        </p>
                      );
                    })}
                  </div>
                </Fade>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}

export default SkillSection;
